import React, { Component } from 'react'
import axios from 'axios'
class Logineeee extends Component {
	constructor(props) {
		super(props)
let loggedIn=false
		this.state = {
			username: '',
			password: '',
 loggedIn
};
this.submitHandler = this.submitHandler.bind(this);
this.changeHandler = this.changeHandler.bind(this);
	}

	changeHandler = e => {
		this.setState({ [e.target.name]: e.target.value })
	}

	submitHandler = e => {
		e.preventDefault()
		console.log(this.state)
		axios
			.post('http://127.0.0.1:8000/api/log_in/', this.state)
			.then(response => {
        localStorage.setItem("x",response.data.refresh)
        this.setState({
          loggedIn:true
        })

			})
			.catch(error => {
				alert("mot de pass incorect")
			})
	}
  logForm(e){
fetch("http://127.0.0.1:8000/api/log_in/",{

 method:"POST",
 body:JSON.stringify(this.state)
})


  }

	render() {
		const { username, password } = this.state
    if(this.state.loggedIn){
      return <Redirect to="/Patients"/>

    }
		return (
			<div>
				<form onSubmit={this.submitHandler}>
					<div>
						<input
							type="text"
							name="username"
							value={username}
							onChange={this.changeHandler}
						/>
					</div>
					<div>
						<input
							type="text"
							name="password"
							value={password}
							onChange={this.changeHandler}
						/>
					</div>

					<button type="submit">Submit</button>
				</form>
			</div>
		)
	}
}

export default Logineeee
